this.className=oldClassList.join(' ');return !0};Element.prototype.removeClassName=function(classString){var oldClassList=this.className.split(' ');var newClassList=oldClassList.filter(function(i){return(i!==classString)});this.className=newClassList.join(' ');return !0};var evtMouseMove=new Event('mousemove');var evtMouseOver=new Event('mouseover');var evtClick=new Event('click');var divNetflixPlayer=document.getElementById('netflix-player');var divControlBar=document.querySelector('#netflix-player > div.player-controls-wrapper.player-active.no-select.opacity-transparent.display-none');var divEpSelect2=document.querySelector('div.player-control-button.player-episode-selector.container-icon-player-episode-selector');function wakeNetflixPlayer(){divNetflixPlayer.dispatchEvent(evtMouseMove)};function wakeNetflixControls(){divControlBar.removeClassName('opacity-transparent');divControlBar.removeClassName('display-none')};function wakeNetflixEpisodes(){divEpSelect2.dispatchEvent(evtMouseOver)};function getEpisodeIdsForCurrentSeason(){var epEls=document.querySelectorAll('ul.episode-list>li.episode-list-item');var epCount=epEls.length;var epIDs=[];for(var i=0;i<epCount;i++){var x=epEls[i];epIDs.push(x.getAttribute('data-episode-id'))}
